mapper.select:
SELECT * from
SELECT * ,#{item.chVersionName} as ch_version_name FROM aps_immediately_inventory WHERE version = #{item.version}
AS derived_table ORDER BY f_lot, version DESC limit #{pass},#{size};

SELECT inv.id, inv.f_material_id, map.f_material_name, f_stock_name, f_base_qty, f_avb_qty, f_lot, f_expiry_date, ch_version_name as version FROM
SELECT * ,#{item.chVersionName} as ch_version_name FROM aps_immediately_inventory WHERE version = #{item.version}
AS inv LEFT JOIN aps_material_name_mapping AS map ON inv.f_material_id = map.f_material_id
