mapper.select:
SELECT bom.id, bom.f_material_id, map.f_material_name, f_document_status, bom.f_material_id_child, map1.f_material_name AS f_material_name_child, f_replace_group_bop, f_numerator, f_denominator, f_fix_scrap_qty_lot, f_material_type, f_replace_type, f_scrap_rate, process, bom_version, ch_version_name AS version FROM ( SELECT *, CASE
WHEN version = #{item.version} THEN #{item.chVersionName}
ELSE NULL END AS ch_version_name FROM aps_material_bom WHERE version IN
#{item.version}
) AS bom LEFT JOIN aps_material_name_mapping AS map ON bom.f_material_id = map.f_material_id LEFT JOIN aps_material_name_mapping AS map1 ON bom.f_material_id_child = map1.f_material_id ORDER BY bom.version desc

mapper.insert:
INSERT INTO `aps_material_bom` ( f_material_id, f_document_status,f_material_id_child, f_replace_group_bop,f_numerator,f_denominator, f_fix_scrap_qty_lot,f_material_type,f_replace_type, f_scrap_rate,process,bom_version, version ) SELECT f_material_id, f_document_status,f_material_id_child, f_replace_group_bop,f_numerator,f_denominator, f_fix_scrap_qty_lot,f_material_type,f_replace_type, f_scrap_rate,process,bom_version, (SELECT MAX(version) + 1 FROM `aps_material_bom`) FROM `aps_material_bom` WHERE -- 根据您的条件生成查询条件，注意使用了删除列表的条件
(
(f_material_id = #{item.FParentMaterialId} AND f_material_id_child = #{item.FMATERIALIDCHILD})
) AND
version = (SELECT MAX(version) FROM `aps_material_bom`);
