mapper.select:
SELECT * from
SELECT * ,#{item.chVersionName} as ch_version_name FROM aps_production_material WHERE version = #{item.version}
AS derived_table ORDER BY f_lot, version DESC limit #{pass},#{size};

SELECT mat.id, mat.f_material_id, map.f_material_name, f_mo_bill_no, mat.f_material_id2, map1.f_material_name as f_material_name2, f_material_type, f_must_qty, f_picked_qty, f_good_return_qty, f_process_defect_return_qty, ch_version_name as version FROM
SELECT * ,#{item.chVersionName} as ch_version_name FROM aps_production_material WHERE version = #{item.version}
AS mat LEFT JOIN aps_material_name_mapping AS map ON mat.f_material_id = map.f_material_id LEFT JOIN aps_material_name_mapping AS map1 ON mat.f_material_id2 = map1.f_material_id
