mapper.select:
SELECT * from
SELECT * ,#{item.chVersionName} as ch_version_name FROM aps_production_order WHERE version = #{item.version}
AS derived_table ORDER BY f_lot, version DESC limit #{pass},#{size};

SELECT ord.id, f_bill_no, f_bill_type, f_bill_type_id, ord.f_material_id, map.f_material_name, f_qty, f_status, f_pick_mtrl_status, f_stock_in_qua_aux_qty, f_bom_id, planned_completion_time, ord.ch_version_name as version FROM
SELECT * ,#{item.chVersionName} as ch_version_name FROM aps_production_order WHERE version = #{item.version}
AS ord LEFT JOIN aps_material_name_mapping AS map ON ord.f_material_id = map.f_material_id
