mapper.select:
SELECT * from
SELECT * ,#{item.chVersionName} as ch_version_name FROM aps_receive_notice WHERE version = #{item.version}
AS derived_table ORDER BY f_lot, version DESC limit #{pass},#{size};

SELECT noti.id, f_bill_no, noti.f_material_id, map.f_material_name, f_must_qty, f_check_qty, f_receive_qty, f_csn_receive_base_qty, f_in_stock_qty, ch_version_name AS version FROM ( SELECT *, CASE
WHEN version = #{item.version} THEN #{item.chVersionName}
ELSE NULL END AS ch_version_name FROM aps_receive_notice WHERE version IN
#{item.version}
) AS noti LEFT JOIN aps_material_name_mapping AS map ON noti.f_material_id = map.f_material_id ORDER BY noti.version desc
