mapper.select:
SELECT tests.id,production_order_number,material_code, map.f_material_name as material_name,burn_in_completion_quantity,burn_qualified_count, burn_fixture_number,burn_fixture_id,total_number,un_burn_qualified_count, ch_version_name AS version FROM ( SELECT *, CASE
WHEN version = #{item.version} THEN #{item.chVersionName}
END AS ch_version_name FROM aps_tfmini_s_calibration_tests WHERE version IN
#{item.version}
) AS tests LEFT JOIN aps_material_name_mapping AS map ON tests.material_code = map.f_material_id ORDER BY tests.version desc
